mapper.delete:
DELETE FROM review_like
WHERE review_id = #{reviewId}
  AND user_id = #{userId}
  AND review_type = #{reviewType}

mapper.select:
SELECT COUNT(*) > 0
FROM review_like
WHERE review_id = #{reviewId}
  AND user_id = #{userId}
  AND review_type = #{reviewType}

SELECT COUNT(*)
FROM review_like
WHERE review_id = #{reviewId}
  AND review_type = #{reviewType}

SELECT review_id
FROM review_like

    review_type = #{reviewType}
    AND user_id = #{userId}

        AND review_id IN

            #{id}

SELECT review_id, COUNT(*) AS cnt
FROM review_like
WHERE review_type = #{reviewType}
  AND review_id IN

      #{id}

GROUP BY review_id
