mapper.select:
SELECT tr.id, tr.rating, tr.content, tr.blinded, tr.created_at, tr.critic, tr.user_id, tr.track_id, u.id AS reviewer_id, u.nickname AS reviewer_nickname, u.profile_image AS reviewer_profile_image, u.is_pro AS reviewer_is_pro, (SELECT COUNT(*) FROM review_like WHERE review_id = tr.id AND review_type = 'TRACK') AS likes, (SELECT COUNT(*) FROM review_report WHERE review_id = tr.id AND review_type = 'TRACK') AS dislikes FROM track_review tr JOIN user u ON u.id = tr.user_id
tr.track_id = #{trackId}
ORDER BY tr.critic DESC, likes DESC, tr.created_at ASC LIMIT #{size} OFFSET #{offset}

SELECT tr.id, tr.rating, tr.content, tr.blinded, tr.created_at, tr.critic, tr.user_id, tr.track_id, u.id AS reviewer_id, u.nickname AS reviewer_nickname, u.profile_image AS reviewer_profile_image, u.is_pro AS reviewer_is_pro, (SELECT COUNT(*) FROM review_like WHERE review_id = tr.id AND review_type = 'TRACK') AS likes, (SELECT COUNT(*) FROM review_report WHERE review_id = tr.id AND review_type = 'TRACK') AS dislikes, t.title FROM track_review tr JOIN user u ON u.id = tr.user_id JOIN track t ON tr.track_id = t.id WHERE tr.user_id = #{loginUserId}
AND ( t.title LIKE CONCAT('%', #{keyword}, '%') OR tr.content LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY
CASE WHEN t.title LIKE CONCAT(#{keyword}, '%') THEN 0 WHEN t.title LIKE CONCAT('%', #{keyword}, '%') THEN 1 WHEN tr.content LIKE CONCAT(#{keyword}, '%') THEN 2 WHEN tr.content LIKE CONCAT('%', #{keyword}, '%') THEN 3 ELSE 4 END, tr.created_at DESC
tr.created_at DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*)
FROM track_review tr
WHERE tr.user_id = #{loginUserId}

mapper.update:
UPDATE track_review
SET rating  = #{rating},
    content = #{content}
WHERE id = #{id}

mapper.select:
SELECT COUNT(*) > 0
FROM track_review
WHERE user_id = #{userId}
AND track_id = #{trackId}

SELECT COUNT(*) FROM track_review
WHERE track_id = #{trackId}
AND blinded = 0
