mapper.select:
SELECT u.id, u.nickname, u.profile_image, uf.created_at FROM user_follow uf INNER JOIN user u ON uf.follower_id = u.id WHERE following_id = #{id}
AND ( u.nickname LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY
CASE WHEN u.nickname LIKE CONCAT(#{keyword}, '%') THEN 0 WHEN u.nickname LIKE CONCAT('%', #{keyword}, '%') THEN 1 ELSE 2 END, uf.created_at DESC
uf.created_at DESC
LIMIT #{offset}, #{limit}

SELECT u.id, u.nickname, u.profile_image, uf.created_at FROM user_follow uf INNER JOIN user u ON uf.following_id = u.id WHERE follower_id = #{id}
AND ( u.nickname LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY
CASE WHEN u.nickname LIKE CONCAT(#{keyword}, '%') THEN 0 WHEN u.nickname LIKE CONCAT('%', #{keyword}, '%') THEN 1 ELSE 2 END, uf.created_at DESC
uf.created_at DESC
LIMIT #{offset}, #{limit}

mapper.delete:
DELETE FROM user_follow
WHERE follower_id = #{loginUserId}
AND following_id = #{id}

mapper.select:
SELECT COUNT(*)
FROM user_follow
WHERE follower_id = #{loginUserId}
AND following_id = #{id}

mapper.update:
UPDATE user_follow
SET created_at = #{createdAt},
    follower_id = #{followerId},
    following_id = #{followingId}
WHERE id = #{id}
